CREATE TABLE `attractions` (
  `att_id` int(11) NOT NULL COMMENT 'รหัสสถานที่',
  `att_name` varchar(255) NOT NULL COMMENT 'ชื่อสถานที่',
  `att_type_id` int(11) NOT NULL COMMENT 'รหัสประเภท',
  `att_description` text NOT NULL COMMENT 'คำอธิบายสถานที่',
  `att_article` longtext NOT NULL COMMENT 'บทความสถานที่',
  `att_image` text NOT NULL COMMENT 'รูปภาพหลักสถานที่',
  `att_google_map` text NOT NULL COMMENT 'แผนที่กูเกิ้ลแมพ',
  `att_cost` int(1) NOT NULL COMMENT 'ค่าใช้จ่าย 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_convenience` int(1) NOT NULL COMMENT 'การเข้าถึง 1 = ไม่สะดวก, 2 = สะดวกน้อย, 3 = สะดวกปานกลาง, 4 = สะดวกมาก',
  `att_restaurant` int(1) NOT NULL COMMENT 'ร้านอาหาร 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_transfer` int(1) NOT NULL COMMENT 'บริการรถรับส่ง 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_hotel` int(1) NOT NULL COMMENT 'บริการที่พักโรงแรม 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_cafe` int(1) NOT NULL COMMENT 'ร้านคาเฟ่ 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_security` int(1) NOT NULL COMMENT 'บริการด้านความปลอดภัย 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_hospital` int(1) NOT NULL COMMENT 'โรงพยาบาล\r\n1 = ไม่ใกล้, 2 = ใกล้น้อย, 3 = ใกล้ปานกลาง, 4 = ใกล้มาก\r\n',
  `att_police` int(1) NOT NULL COMMENT 'สถานีตำรวจ 1 = ไม่ใกล้, 2 = ใกล้น้อย, 3 = ใกล้ปานกลาง, 4 = ใกล้มาก',
  `att_parking` int(1) NOT NULL COMMENT 'ที่จอดรถ 1 = ไม่มี, 2 = มีน้อย, 3 = มีปานกลาง, 4 = มีมาก',
  `att_views` int(11) NOT NULL COMMENT 'ยอดผู้เข้าชม',
  `att_created` timestamp NOT NULL DEFAULT current_timestamp() COMMENT 'วันที่สร้างข้อมูล',
  `att_updated` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT 'วันที่อัพเดตข้อมูลล่าสุด'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

